def solve_tachyon_manifold(puzzle_input):
    raw_lines = puzzle_input.strip().strip('"').split('\n')
    grid = [line for line in raw_lines if line.strip()]
    
    if not grid:
        return 0
    
    height = len(grid)
    width = len(grid[0])
    
    start_col = -1
    for col, char in enumerate(grid[0]):
        if char == 'S':
            start_col = col
            break
            
    if start_col == -1:
        return "Error: Start point 'S' not found."

    current_beams = {start_col}
    split_count = 0
    
    for r in range(height):
        next_beams = set()
        
        for c in current_beams:
            if c < 0 or c >= width:
                continue
            
            cell = grid[r][c]
            
            if cell == '^':
                split_count += 1
                next_beams.add(c - 1)
                next_beams.add(c + 1)
            else:
                next_beams.add(c)
        
        current_beams = next_beams
        
        if not current_beams:
            break

    return split_count

